css:
.tariff{
    margin-top:20px;
}
.sep-head{
    background:#5eff47a3;
    font-size: 0.8rem;
}

.table-hd{
    font-weight:bold !important; 
}

.print-format .table-bordered td, .print-format .table-bordered th {
  border: 1px solid #f9a7a7;
}
.tail-total{
    font-style:italic;
}
.cus-tot{
    text-decoration:underline;
    font-weight:bold;
    font-style:italic;
}
.u-b-heading{
    font-weight:bold;
    text-decoration:underline;
}
.und-lin{
    text-decoration:underline;
}
.marg{
    margin-top:20px;
}
td{
    font-size: 0.7rem;
}
.summary{
    font-weight:600;
    font-style: italic;
}
.blank{
    height:37px;
}

html:
{% set receipts = custom_entry_print_data(doc.name) %}
{% set tariff_count1 = namespace(value=custom_entry_print_data(doc.name,True)) %}
{% set tariff_count = namespace(value=1) %}
{% for receipt in receipts %}
    <!--<div>receipt--{{receipt}}</div>-->
    {% for purchase in receipt %}
        {% set data = receipt.get(purchase) %}
        {% set tariff_det=data.get('data') %}
        {% set tariff_keys=data.get('tariff_keys') %}
        <div class="group1 marg">
        {{purchase}}
            <div><span class="und-lin">Supplier Name: </span>{{data.get("supplier_name")}}</div>
            <div><span class="und-lin">Supplier Country: </span>{{data.get("supplier_country")}}</div>
            <div><span class="und-lin">Invoice Number:</span> {{data.get("invoice_number")}}</div>
        </div>
        <table class="table table-bordered">
        	<tbody>
        		<tr>
        			<th class="table-hd">Line No</th>
        			<th class="table-hd">Item No</th>
        			<th class="table-hd">Description</th>
        			<th class="table-hd">Tariff Code</th>
        			<th class="table-hd">Qty</th>
        			<th class="table-hd" style="width: 10%;">FOB/CIF Price(Unit)</th>
        			<th class="table-hd">Misc (charges) <span style="font-style:italic;">{{data.get("cur_pur")}}</span></th>
        			<th class="table-hd" style="width: 10%;">FOB/CIF Price(Total)</th>
        		</tr>


        	   {% set count = namespace(value=1) %}
               {% set amount = namespace(value=0) %}
               {% set amount_c = namespace(value=0) %}
               {% set t_qty = namespace(value=0) %}
               {% set mass = frappe.db.get_value('Customs Entry',doc.name, ['mass_kg']) %}
               {% if mass %}
                {% set mass_split = mass/tariff_count1.value %}
                {% else %}
                {% set mass_split = 0 %}
               {% endif %}
               {% set add_chr= additional_charges(doc.name) %}

               {%- for tariff in tariff_keys -%} 
                   {% set tariff_data = frappe.db.get_value('Customs Tariff Number',tariff, ['description','custom_duty_percentage','custom_surcharge_percentage','custom_service_charge_percentage','custom_excise_percentage','custom_vat_percentage']) %}
                   {% set curr =namespace(value="") %}
                   {% set amount.value = 0 %}
                   {% set amount_c.value = 0 %}
                   {% set t_qty.value = 0 %}
                    <tr>
                        <td class="sep-head" colspan=8>Asycuda No: {{tariff_count.value}} --- Code : {{tariff}}{{tariff_data[1],tariff_data[2],tariff_data[3],tariff_data[4],tariff_data[5]}} --- {{tariff_data[0]}}</td>
                    </tr>
                    {%- for items in tariff_det.get(tariff) -%}
                        {% set curr.value = items.ref_document_currency %}
                		<tr>
                			<td >{{count.value}}</td>
                			<td >{{items.item}}</td>
                			<td >{{items.description}}</td>
                			<td >{{items.customs_tariff_number}}</td>
                			<td >{{items.qty}}</td>
                			<td >{{items.base_rate}}</td>
                			<td >{{items.miscellaneous|round(3)}}</td>
                			<td >{{items.base_amount|round(3)}}</td>
                        </tr>
                        {% set count.value = count.value + 1 %}
                        {% set t_qty.value = t_qty.value + items.qty %}
                        {% set amount.value = amount.value + items.base_amount %}
                        {% set amount_c.value = amount_c.value + items.amount %}
                    {% endfor %}
                    {% set curr_sym= frappe.db.get_value('Currency',curr.value, ['symbol']) %}
                    <tr>
                            <td class="summary" colspan=6>&nbspTotal</td>
                			<td class="summary"></td>
                			<td class="summary">{{curr.value}} {{curr_sym}}{{amount.value|round(2)}}</td>
                    </tr>
                    {% if loop.last %}
                        <tr>
                            <td class="summary" colspan=6>&nbspTotal</td>
                			<td class="summary">{{data.get("frn_curr_sym")}}{{data.get("taxes_charges")}}</td>
                			<td class="summary">{{data.get("frn_curr_sym")}}{{data.get("cur_frn")|round(2)}}</td>
                        </tr>
                    {% endif %}
                    {% set tariff_count.value = tariff_count.value + 1 %}
                {% endfor %}
        	</tbody>
        </table>

        <div class="page-break"></div>
    {% endfor %}
{% endfor %}
