css:
.tariff{
    margin-top:20px;
}
.sep-head{
    background:#deefd8;
    font-size: 14px;
}

.table-hd{
    font-weight:bold !important; 
}

.print-format .table-bordered td, .print-format .table-bordered th {
  border: none;
}

tr{
    border:2px solid #e6d8d8;;
}
.tail-total{
    font-style:italic;
}
.cus-tot{
    text-decoration:underline;
    font-weight:bold;
    font-style:italic;
}
.u-b-heading{
    font-weight:bold;
    text-decoration:underline;
}
.und-lin{
    text-decoration:underline;
}
.marg{
    margin-top:20px;
}
.summary-top{
    display:grid;
    grid-template-columns: repeat(4,1fr);
    font-size: 11px;
}
.description-head{
    display: flex;
}
.head2{
    flex-grow:1;
    text-align: center;
}
.head1{
    min-width: 45px;
}
.summary-top div{
    text-align:center;
}
.sep-tail{
    text-align: center;
    background:#d9ecf7;
}
.heading-line{
    background:#d9ecf7;
}
.print-format th {
  color: #24272b;

}
.group1{
    font-size: 15px;
}
