css:
.footer {
  position: fixed;
  left: 0;
  bottom: 0;
  width: 100%;
}
.textcenter{
        text-align:center;
}
.borderclass{
    border-bottom: 1px solid dodgerblue;
}
table {
    width:100%;
    text-align:left;
    border-collapse: collapse;
}

table, td, th {
    /*width:50%;*/
    border: 1px solid dodgerblue;

    font-size:10px;

}
.print-format{
    padding: 12px !important;
    margin-bottom: 0px;
    margin-top:0px;
}

html:
{%- set udts = get_unique_invoice(doc.items) -%}
{%- set gdc = get_document_count(doc.items,doc.currency) -%}

{% for d in udts %}



        <table>
    <tr>
        <td>
            {% if letter_head %}
                {{ letter_head }}
            {% endif %}
        </td>
    </tr>
</table>
<table>
    <tr>
         {%- for i in doc.items -%}
          {% if i.idx == 1 %}
            {% if i.reference_doctype == 'Purchase Invoice'%}
                <td style="text-align: center; font-size: 12px;"><b>PURCHASE INVOICE</b></td>{% endif %}
            {% if i.reference_doctype == 'Purchase Receipt'%}
                <td style="text-align: center; font-size: 12px;"><b>PURCHASE RECEIPT</b></td>{% endif %}
        {% endif %}
        {% endfor %}
    </tr>
</table>
<div style="width:100%;font-size: 15px;">
    <table>
        <tr>
            <td style="width: 50%;">
                <div class="row">
                    <div class="col-xs-12">
                        <b>{{ _("PO No.") }}:</b>&nbsp; <span style="font-size: 10px"><b>

                        {{d}}
                        </b></span>
                    </div>

                </div>
            </td>
            <td style="width: 50%;">
                <div class="row">
                    <div class="col-xs-12">
                        {%- for i in doc.items -%}

                            {% if i.idx == 1 %}
                            {%if i.reference_doctype == "Purchase Invoice" %}
                            {%- set b_address=frappe.get_doc("Purchase Invoice",d) -%}
                            {% endif %}

                             {%if i.reference_doctype == "Purchase Receipt" %}
                             {%- set b_address=frappe.get_doc("Purchase Receipt",d) -%}
                             {% endif %}

                        <b>{{ _("PO Date") }}:</b>&nbsp;{{ b_address.posting_date.strftime('%d/ %m/ %Y') }}
                        {% endif %}
                         {% endfor %}
                    </div>

                </div>


            </td>
        </tr>
</table>

<table>


        <tr>
            <td class="text-center" style="width: 50%;">
                <b>{{ _("Shipping Address")}}</b>
            </td>
            <td class="text-center" style="width: 50%;">
                <b>{{ _("Billing Address")}}</b>
            </td>
        </tr>
        <tr style="width: 50%;">

            <td>
                {% set b_address %}{% endset %}
                {% set b_add %}{% endset %}
                 {% set bill %}{% endset %}


                {% set vars = {'jcase': 0} %}
                {%- for i in doc.items -%}
                {% if i.idx == 1 %}
                {%if i.reference_doctype == "Purchase Invoice" %}
                {%- set b_address=frappe.get_doc("Purchase Invoice",d) -%}
                  {% if b_address.shipping_address or b_address.billing_address %}
                {%- set b_add = frappe.get_doc("Address",b_address.shipping_address) -%}
                {%- set bill = frappe.get_doc("Address",b_address.billing_address) -%}
                {% endif %} {% endif %}

                 {%if i.reference_doctype == "Purchase Receipt" %}
                 {%- set b_address=frappe.get_doc("Purchase Receipt",d) -%}
                 {% if b_address.shipping_address or b_address.billing_address %}
                {%- set b_add = frappe.get_doc("Address",b_address.shipping_address) -%}
                {%- set bill = frappe.get_doc("Address",b_address.billing_address) -%}
                 {% endif %}{% endif %}

                <b>{{ _("Supplier Name") }}:&nbsp;
                {% if b_add.address_title %}
                    {{b_add.address_title}}
                {% endif %} </b><br>
                <b>{{ _("Address") }}:</b>&nbsp; 
                {% if b_add.address_line1 %} 
                    {{b_add.address_line1}}, 
                {% endif %}<br>
                {% if b_add.address_line2 %} 
                    {{b_add.address_line2}} ,
                {% endif %}<br>
                {% if b_add.city %} 
                    {{b_add.city}} 
                {% endif %}
                {% if b_add.pincode %} 
                    -{{b_add.pincode}}, 
                {% endif %}
                {% if b_add.gst_state %} 
                    {{b_add.gst_state}}, 
                {% endif %}
                {% if b_add.country %} 
                    {{b_add.country}}.
                {% endif %}<br>
                <b>M.No.:</b>&nbsp;
                {% if b_add.phone %}
                    {{b_add.phone}}
                {% endif %}<br>
                <b>Email:</b>&nbsp;
                {% if b_add.email_id %}
                    {{b_add.email_id}} 
                {% endif %}<br>


            </td>

            <td style="width: 50%;">
                <b>{{ _("Name") }}:&nbsp;
                {% if bill.address_line1 %}
                        {{bill.address_title}}
                    {% endif %}
                </b><br>
                <b>{{ _("Address") }}:</b>&nbsp; 
                    {% if bill.address_line1 %} 
                        {{bill.address_line1}}, 
                    {% endif %}
                <br>
                {% if bill.address_line2 %} 
                    {{bill.address_line2}}, 
                {% endif %}<br>
                {% if bill.city %} 
                    {{bill.city}} 
                {% endif %}
                {% if bill.pincode %} 
                    -{{bill.pincode}}, 
                {% endif %}
                {% if bill.gst_state %} 
                    {{bill.gst_state}}, 
                {% endif %}
                {% if bill.country %} 
                    {{bill.country}}. 
                {% endif %}<br>

                <b>M.No.:</b>&nbsp;
                {% if bill.phone %}
                    {{bill.phone}}
                {% endif %}<br>
                <b>Email:</b>&nbsp;
                {% if bill.email_id %}
                    {{bill.email_id}} 
                {% endif %}<br>

            </td>
        </tr>

    </table>



        <table style="margin-top: 2px;">
            <tr>
                <th><b>{{ _("Sr No.") }}</b></th>
                <th><b>{{ _("Item Name") }}</b></th>
                <th><b>{{ _("Reference Doctype") }}</b></th>
                <th><b>{{ _("Customs Tariff Number") }}</b></th>
                <th><b>{{ _("Quantity") }}</b></th>
                <th><b>{{ _("Rate") }}</b></th>

                <th style="width:12%;"><b>{{ _("Amount") }}</b></th>
            </tr>

             {%- for k in gdc -%}
             {% if d == k.reference_document %}

            <tr>


                <td style="text-align: center;">{{k.sr_no}}</td>


                <td>{{ k.item_name }}
                </td>

                <td>
                    {% if k.reference_doctype %}
                        {{ k.reference_doctype }}
                    {% endif %}
                </td>
                <td>
                    {{k.customs_tariff_number}}
                </td>

                <td style="text-align: center;">
                    {{ k.qty }}
                </td>
                <td style="text-align: right;">{{ k.rate}}</td>


                <td style="text-align: right;width:12%;">{{ k.amount}}</td>
            </tr>

            {% endif %}
            {%- endfor -%}


            <tr>

                <th></th>
                <th></th>
                <th></th>
                <th style="text-align: right;"><b>{{ _("Total") }}</b></th>
                <th></th>
                <th></th>
                {% for j in doc.consolidation%}
                    {% if j.reference_document == d%}
                        {% if i.idx == 1 %}
                            <th style="text-align: right;width:12%;"><b>{{get_currency_symbol(j.fob_price,doc.currency)}}</b></th>
                        {% endif %}
                    {% endif %}
                {% endfor %} 
            </tr>

        </table>



    <table style="margin-top: 2px;">
         {% for j in doc.consolidation%}
            {% if j.reference_document == d%}
                {% if i.idx == 1 %}
        <tr>
            <td rowspan ="{{ rowspan }}" style ="width:57%;">
                <b>Total Amount in Words:</b><br>{{ frappe.utils.money_in_words(j.fob_price) }}
            </td>
            <td style ="width:28%;" class ="text-right">
                <b>Total Amount:</b>
            </td>
            {% if d%}
            <td style ="width:11%;" class ="text-right">{{get_currency_symbol(j.fob_price,doc.currency)}}</td>
            {% endif%}
        </tr>


                {% endif %}
            {% endif %}

        {% endfor %} 
    </table>


</div>


     {% endif %}
    {% endfor %}


  <div style="page-break-before: always;"></div>   
 {% endfor %}
